import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import html2canvas from 'html2canvas';
import toast from 'react-hot-toast';
import './component.css';

const MemeGenerator = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [cloudinaryId, setCloudinaryId] = useState('');
  const [topText, setTopText] = useState('');
  const [bottomText, setBottomText] = useState('');
  const [title, setTitle] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [preview, setPreview] = useState(null)
  const memeRef = useRef(null);

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (file.size > 5 * 1024 * 1024) {
      toast.error('Le fichier est trop volumineux (max 5MB)');
      return;
    }

    setSelectedImage(file);
    setPreview( URL.createObjectURL(file))
    if (file){
        setIsUploading(true);
    }
  };
  
  const generateMemeImage = async (imageFile, topText, bottomText) => {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");
  
        canvas.width = img.width;
        canvas.height = img.height;
  
        // Dessiner l'image de base
        ctx.drawImage(img, 0, 0);
  
        // TOP TEXT
        const topFontSize = Math.floor(canvas.width / 10);
        ctx.font = `bold ${topFontSize}px Impact`;
        ctx.textAlign = "center";
        ctx.textBaseline = "top";
        ctx.fillStyle = "white";
        ctx.strokeStyle = "black";
        ctx.lineWidth = 6;
  
        ctx.strokeText(topText, canvas.width / 2, 20);
        ctx.fillText(topText, canvas.width / 2, 20);
  
        // BOTTOM TEXT avec fond noir
        const bottomFontSize = topFontSize;
        const bottomTextY = canvas.height / 2;
        const textHeight = bottomFontSize + 20;
  
        ctx.fillStyle = "black";
        ctx.fillRect(0, bottomTextY - 10, canvas.width, textHeight);
  
        ctx.font = `bold ${bottomFontSize}px Impact`;
        ctx.textAlign = "center";
        ctx.textBaseline = "top";
        ctx.fillStyle = "white";
        ctx.strokeStyle = "black";
        ctx.lineWidth = 3;
  
        ctx.strokeText(bottomText, canvas.width / 2, bottomTextY);
        ctx.fillText(bottomText, canvas.width / 2, bottomTextY);
  
        // Preview mise à jour ici
        const dataURL = canvas.toDataURL("image/png");
        setPreview(dataURL); // pour affichage live
        console.log("kd", dataURL);
        
  
        canvas.toBlob((blob) => {
          if (blob) resolve(blob);
          else reject(new Error("Échec génération mème"));
        }, "image/png");
      };
  
      img.onerror = reject;
      img.src = URL.createObjectURL(imageFile);
      console.log(img.src);
    //   setPreview(img.src);
      setSelectedImage(imageFile)
      
    });
  };
  
  useEffect(() => {
    if (selectedImage && topText && bottomText) {
      generateMemeImage(selectedImage, topText, bottomText)
        .then(blob => {
          const url = URL.createObjectURL(blob);
          setPreview(url);
        });
    }
  }, [selectedImage, topText, bottomText]); 

const generateFinalMemeAndUpload = async () => {
    if (!memeRef.current) return;
  
    setIsUploading(true);
    try {
    const blob = await generateMemeImage(selectedImage, topText, bottomText);
  
      const formData = new FormData();
      formData.append('image', blob, 'meme.png');
  
      const response = await axios.post("http://localhost:5000/api/upload", formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
  
      setImageUrl(response.data.imageUrl);
      setCloudinaryId(response.data.cloudinaryId);
      toast.success('Mème généré et uploadé!');
      return true;
    } catch (error) {
      console.error('Erreur lors de la génération:', error);
      toast.error('Erreur pendant le rendu du mème');
      return false;
    } finally {
      setIsUploading(false);
    }
  };
  
const handleSaveMeme = async () => {
    if (!title.trim()) {
      toast.error('Veuillez ajouter un titre');
      return;
    }
    const success = await generateFinalMemeAndUpload();
    if (!success || !imageUrl) {
      toast.error("L'image avec le texte n'a pas pu être générée.");
      return;
    }
    setIsSaving(true);
    try {
      const memeData = {
        title: title.trim(),
        imageUrl,
        cloudinaryId,
      };
      await axios.post('http://localhost:5000/api/memes', memeData);
      toast.success('Mème sauvegardé avec succès!');
      resetForm();
    } catch (error) {
      console.error('Erreur sauvegarde:', error);
      toast.error('Erreur lors de la sauvegarde');
    } finally {
      setIsSaving(false);
    }
  };
  
const handleDownload = async () => {
    if (!memeRef.current) return;

    try {
      const canvas = await html2canvas(memeRef.current, {
        backgroundColor: null,
        scale: 2,
      });

      const link = document.createElement('a');
      link.download = `${title || 'meme'}.png`;
      link.href = canvas.toDataURL();
      link.click();
      
      toast.success('Mème téléchargé!');
    } catch (error) {
      console.error('Erreur téléchargement:', error);
      toast.error('Erreur lors du téléchargement');
    }
  };

  const resetForm = () => {
    setSelectedImage(null);
    setImageUrl('');
    setCloudinaryId('');
    setTopText('');
    setBottomText('');
    setTitle('');
  };

  const handleShare = async () => {
    if (!preview) return;
  
    try {
      const response = await fetch(preview);
      const blob = await response.blob();
      const file = new File([blob], 'meme.png', { type: blob.type });
  
      if (navigator.canShare && navigator.canShare({ files: [file] })) {
        await navigator.share({
          title: `Mème: ${title}`,
          text: `Regardez ce mème que j'ai créé : ${title}`,
          files: [file],
        });
      } else {
        navigator.clipboard.writeText(preview);
        toast.success('URL copiée dans le presse-papier (partage direct non supporté)');
      }
    } catch (error) {
      console.error("Erreur lors du partage:", error);
      toast.error("Partage impossible.");
    }
  };
  

return (
    <div className="meme-generator">
      <div className="generator-container">
        <h1>Créateur de Mèmes</h1>
        <div className="generator-content">

          {/* Section Upload */}
          <div className="upload-section">
            <h2>1. Choisir une image</h2>
            <div className="upload-area">
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                className="file-input"
                id="image-upload"
              />
              <label htmlFor="image-upload" className="upload-label">
                {/* {isUploading ? 'Upload en cours...' : '📤 Choisir une image'} */}
              </label>
            </div>
          </div>

          {/* Section Texte */}
          {selectedImage && (
            <div className="text-section">
              <h2>2. Ajouter du texte</h2>
              <div className="input-group">
                <input
                  type="text"
                  placeholder="Titre du mème"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  className="text-input"
                />
              </div>
              <div className="input-group">
                <input
                  type="text"
                  placeholder="Texte du haut"
                  value={topText}
                  onChange={(e) => setTopText(e.target.value)}
                  className="text-input"
                />
              </div>
              <div className="input-group">
                <input
                  type="text"
                  placeholder="Texte du bas"
                  value={bottomText}
                  onChange={(e) => setBottomText(e.target.value)}
                  className="text-input"
                />
              </div>
            </div>
          )}

          {/* Aperçu */}
          {selectedImage && (
            <div className="">
              <h2>3. Aperçu</h2>
              <div className="" ref={memeRef}>
                <div className="meme-container">
                {selectedImage && (
                <img src={preview} alt="preview" style={{ maxWidth: "400px" }} />
                )}
                </div>
              </div>

              {/* Actions */}
              <div className="actions">
                <button
                  onClick={handleSaveMeme}
                  disabled={isSaving}
                  className="action-btn save-btn"
                >
                  {isSaving ? 'Sauvegarde...' : '💾 Sauvegarder'}
                </button>
                <button
                  onClick={handleDownload}
                  className="action-btn download-btn"
                >
                  📥 Télécharger
                </button>
                <button
                  onClick={handleShare}
                  className="action-btn share-btn"
                >
                  📤 Partager
                </button>
                <button
                  onClick={resetForm}
                  className="action-btn reset-btn"
                >
                  🔄 Nouveau
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MemeGenerator;
