import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import html2canvas from 'html2canvas';
import toast from 'react-hot-toast';
import './component.css';

const MemeGenerator = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [cloudinaryId, setCloudinaryId] = useState('');
  const [topText, setTopText] = useState('');
  const [bottomText, setBottomText] = useState('');
  const [title, setTitle] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [preview, setPreview] = useState(null)
  const memeRef = useRef(null);

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (file.size > 5 * 1024 * 1024) {
      toast.error('Le fichier est trop volumineux (max 5MB)');
      return;
    }

    setSelectedImage(file);
    setPreview = URL.createObjectURL(selectedImage)
    if (file){
        setIsUploading(true);
    }
  };


const generateMemeImage = async (imageFile, topText, bottomText) => {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");
  
        canvas.width = img.width;
        canvas.height = img.height;
  
        // Dessiner l'image de base
        ctx.drawImage(img, 0, 0);
  
        // Style texte commun
        ctx.font = `bold ${Math.floor(canvas.width / 10)}px Impact`;
        ctx.fillStyle = "white";
        ctx.strokeStyle = "black";
        ctx.lineWidth = 6;
        ctx.textAlign = "center";
  
        // Dessiner le texte du haut
        ctx.textBaseline = "top";
        ctx.strokeText(topText, canvas.width / 2, 20);
        ctx.fillText(topText, canvas.width / 2, 20);
  
        // Dessiner le texte du bas (avec fond noir)
        const textY = canvas.height / 2;
        const textHeight = Math.floor(canvas.width / 10) + 20;
  
        // fond noir rectangulaire
        ctx.fillStyle = "black";
        ctx.fillRect(0, textY - 10, canvas.width, textHeight);
  
        // texte blanc par-dessus
        ctx.fillStyle = "white";
        ctx.textBaseline = "top";
        ctx.fillText(bottomText, canvas.width / 2, textY);
  
        canvas.toBlob((blob) => {
          if (blob) resolve(blob);
          else reject(new Error("Ã‰chec gÃ©nÃ©ration mÃ¨me"));
        }, "image/png");
      };
  
      img.onerror = reject;
      img.src = URL.createObjectURL(imageFile);
      setPreview(img.src)
    });
  };
  
const generateFinalMemeAndUpload = async () => {
    if (!memeRef.current) return;
  
    setIsUploading(true);
    try {
    const blob = await generateMemeImage(selectedImage, topText, bottomText);
  
      const formData = new FormData();
      formData.append('image', blob, 'meme.png');
  
      const response = await axios.post("http://localhost:5000/api/upload", formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
  
      setImageUrl(response.data.imageUrl);
      setCloudinaryId(response.data.cloudinaryId);
      toast.success('MÃ¨me gÃ©nÃ©rÃ© et uploadÃ©!');
      return true;
    } catch (error) {
      console.error('Erreur lors de la gÃ©nÃ©ration:', error);
      toast.error('Erreur pendant le rendu du mÃ¨me');
      return false;
    } finally {
      setIsUploading(false);
    }
  };
  
const handleSaveMeme = async () => {
    if (!title.trim()) {
      toast.error('Veuillez ajouter un titre');
      return;
    }
    const success = await generateFinalMemeAndUpload();
    if (!success || !imageUrl) {
      toast.error("L'image avec le texte n'a pas pu Ãªtre gÃ©nÃ©rÃ©e.");
      return;
    }
    setIsSaving(true);
    try {
      const memeData = {
        title: title.trim(),
        imageUrl,
        cloudinaryId,
      };
      await axios.post('http://localhost:5000/api/memes', memeData);
      toast.success('MÃ¨me sauvegardÃ© avec succÃ¨s!');
      resetForm();
    } catch (error) {
      console.error('Erreur sauvegarde:', error);
      toast.error('Erreur lors de la sauvegarde');
    } finally {
      setIsSaving(false);
    }
  };
  
const handleDownload = async () => {
    if (!memeRef.current) return;

    try {
      const canvas = await html2canvas(memeRef.current, {
        backgroundColor: null,
        scale: 2,
      });

      const link = document.createElement('a');
      link.download = `${title || 'meme'}.png`;
      link.href = canvas.toDataURL();
      link.click();
      
      toast.success('MÃ¨me tÃ©lÃ©chargÃ©!');
    } catch (error) {
      console.error('Erreur tÃ©lÃ©chargement:', error);
      toast.error('Erreur lors du tÃ©lÃ©chargement');
    }
  };

const handleShare = async () => {
    if (navigator.share && imageUrl) {
      try {
        await navigator.share({
          title: `MÃ¨me: ${title}`,
          text: `Regardez ce mÃ¨me que j'ai crÃ©Ã©: ${title}`,
          url: imageUrl,
        });
      } catch (error) {
        console.log('Partage annulÃ©');
      }
    } else {
      // Fallback: copier l'URL
      if (imageUrl) {
        navigator.clipboard.writeText(imageUrl);
        toast.success('URL copiÃ©e dans le presse-papier!');
      }
    }
  };

const resetForm = () => {
    setSelectedImage(null);
    setImageUrl('');
    setCloudinaryId('');
    setTopText('');
    setBottomText('');
    setTitle('');
  };

return (
    <div className="meme-generator">
      <div className="generator-container">
        <h1>CrÃ©ateur de MÃ¨mes</h1>
        <div className="generator-content">

          {/* Section Upload */}
          <div className="upload-section">
            <h2>1. Choisir une image</h2>
            <div className="upload-area">
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                className="file-input"
                id="image-upload"
              />
              <label htmlFor="image-upload" className="upload-label">
                {isUploading ? 'Upload en cours...' : 'ðŸ“¤ Choisir une image'}
              </label>
            </div>
          </div>

          {/* Section Texte */}
          {selectedImage && (
            <div className="text-section">
              <h2>2. Ajouter du texte</h2>
              <div className="input-group">
                <input
                  type="text"
                  placeholder="Titre du mÃ¨me"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  className="text-input"
                />
              </div>
              <div className="input-group">
                <input
                  type="text"
                  placeholder="Texte du haut"
                  value={topText}
                  onChange={(e) => setTopText(e.target.value)}
                  className="text-input"
                />
              </div>
              <div className="input-group">
                <input
                  type="text"
                  placeholder="Texte du bas"
                  value={bottomText}
                  onChange={(e) => setBottomText(e.target.value)}
                  className="text-input"
                />
              </div>
            </div>
          )}

          {/* AperÃ§u */}
          {selectedImage && (
            <div className="preview-section">
              <h2>3. AperÃ§u</h2>
              <div className="meme-preview" ref={memeRef}>
                <div className="meme-container">
                {selectedImage && (
                <img src={preview} alt="preview" style={{ maxWidth: "400px" }} />
                )}
                  {topText && (
                    <div className="meme-text meme-text-top">
                      {topText}
                    </div>
                  )}
                  {bottomText && (
                    <div className="meme-text meme-text-bottom">
                      {bottomText}
                    </div>
                  )}
                </div>
              </div>

              {/* Actions */}
              <div className="actions">
                <button
                  onClick={handleSaveMeme}
                  disabled={isSaving}
                  className="action-btn save-btn"
                >
                  {isSaving ? 'Sauvegarde...' : 'ðŸ’¾ Sauvegarder'}
                </button>
                <button
                  onClick={handleDownload}
                  className="action-btn download-btn"
                >
                  ðŸ“¥ TÃ©lÃ©charger
                </button>
                <button
                  onClick={handleShare}
                  className="action-btn share-btn"
                >
                  ðŸ“¤ Partager
                </button>
                <button
                  onClick={resetForm}
                  className="action-btn reset-btn"
                >
                  ðŸ”„ Nouveau
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MemeGenerator;
