import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import './component.css';

const Gallery = () => {
  const [memes, setMemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedMeme, setSelectedMeme] = useState(null);

  useEffect(() => {
    fetchMemes();
  }, []);

  const fetchMemes = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/memes');
      setMemes(response.data);
    } catch (error) {
      console.error('Erreur récupération mèmes:', error);
      toast.error('Erreur lors du chargement des mèmes');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteMeme = async (memeId) => {
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer ce mème ?')) {
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/api/memes/${memeId}`);
      setMemes(memes.filter(meme => meme._id !== memeId));
      toast.success('Mème supprimé avec succès');
      setSelectedMeme(null);
    } catch (error) {
      console.error('Erreur suppression:', error);
      toast.error('Erreur lors de la suppression');
    }
  };

  const handleShareMeme = async (meme) => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: `Mème: ${meme.title}`,
          text: `Regardez ce mème: ${meme.title}`,
          url: meme.imageUrl,
        });
      } catch (error) {
        console.log('Partage annulé');
      }
    } else {
      navigator.clipboard.writeText(meme.imageUrl);
      toast.success('URL copiée dans le presse-papier!');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('fr-FR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="gallery">
        <div className="loading">
          <div className="spinner"></div>
          <p>Chargement des mèmes...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="gallery">
      <div className="gallery-container">
        <h1>Galerie de Mèmes</h1>
        <p className="gallery-subtitle">
          {memes.length} mème{memes.length !== 1 ? 's' : ''} créé{memes.length !== 1 ? 's' : ''}
        </p>

        {memes.length === 0 ? (
          <div className="empty-gallery">
            <div className="empty-icon">🎭</div>
            <h2>Aucun mème pour le moment</h2>
            <p>Commencez par créer votre premier mème!</p>
            <a href="/" className="create-link">
              Créer un mème
            </a>
          </div>
        ) : (
          <div className="memes-grid">
            {memes.map((meme) => (
              <div
                key={meme._id}
                className="meme-card"
                onClick={() => setSelectedMeme(meme)}
              >
                <div className="meme-preview">
                  <img src={meme.imageUrl} alt={meme.title} />
                  {meme.topText && (
                    <div className="meme-text meme-text-top">
                      {meme.topText}
                    </div>
                  )}
                  {meme.bottomText && (
                    <div className="meme-text meme-text-bottom">
                      {meme.bottomText}
                    </div>
                  )}
                </div>
                <div className="meme-info">
                  <h3>{meme.title}</h3>
                  <p className="meme-date">{formatDate(meme.createdAt)}</p>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Modal de détails */}
        {selectedMeme && (
          <div className="modal-overlay" onClick={() => setSelectedMeme(null)}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <button
                className="modal-close"
                onClick={() => setSelectedMeme(null)}
              >
                ✕
              </button>
              
              <div className="modal-meme">
                <div className="meme-preview large">
                  <img src={selectedMeme.imageUrl} alt={selectedMeme.title} />
                  {selectedMeme.topText && (
                    <div className="meme-text meme-text-top">
                      {selectedMeme.topText}
                    </div>
                  )}
                  {selectedMeme.bottomText && (
                    <div className="meme-text meme-text-bottom">
                      {selectedMeme.bottomText}
                    </div>
                  )}
                </div>
                
                <div className="modal-info">
                  <h2>{selectedMeme.title}</h2>
                  <p className="modal-date">
                    Créé le {formatDate(selectedMeme.createdAt)}
                  </p>
                  
                  <div className="modal-actions">
                    <button
                      onClick={() => handleShareMeme(selectedMeme)}
                      className="modal-btn share-btn"
                    >
                      📤 Partager
                    </button>
                    <a
                      href={selectedMeme.imageUrl}
                      download={`${selectedMeme.title}.jpg`}
                      className="modal-btn download-btn"
                    >
                      📥 Télécharger
                    </a>
                    <button
                      onClick={() => handleDeleteMeme(selectedMeme._id)}
                      className="modal-btn delete-btn"
                    >
                      🗑️ Supprimer
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Gallery;
