import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import html2canvas from 'html2canvas';
import toast from 'react-hot-toast';
import './component.css';

const MemeGenerator = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [cloudinaryId, setCloudinaryId] = useState('');
  const [topText, setTopText] = useState('');
  const [bottomText, setBottomText] = useState('');
  const [title, setTitle] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [preview, setPreview] = useState(null)
  const memeRef = useRef(null);

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (file.size > 5 * 1024 * 1024) {
      toast.error('Le fichier est trop volumineux (max 5MB)');
      return;
    }

    setSelectedImage(file);
    setPreview( URL.createObjectURL(file))
    if (file){
        setIsUploading(true);
    }
  };
  
  const generateMemeImage = async (imageFile, topText, bottomText) => {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");
  
        canvas.width = img.width;
        canvas.height = img.height;
  
        // Dessiner l'image de base
        ctx.drawImage(img, 0, 0);
  
        // TOP TEXT
        const topFontSize = Math.floor(canvas.width / 10);
        ctx.font = `bold ${topFontSize}px Impact`;
        ctx.textAlign = "center";
        ctx.textBaseline = "top";
        ctx.fillStyle = "white";
        ctx.strokeStyle = "black";
        ctx.lineWidth = 6;
  
        ctx.strokeText(topText, canvas.width / 2, 20);
        ctx.fillText(topText, canvas.width / 2, 20);
  
        // BOTTOM TEXT avec fond noir
        const bottomFontSize = topFontSize;
        const bottomTextY = canvas.height / 1.5;
        const textHeight = bottomFontSize + 20;
  
        ctx.fillStyle = "black";
        ctx.fillRect(0, bottomTextY - 10, canvas.width, textHeight);
  
        ctx.font = `bold ${bottomFontSize}px Impact`;
        ctx.textAlign = "center";
        ctx.textBaseline = "top";
        ctx.fillStyle = "white";
        ctx.strokeStyle = "black";
        ctx.lineWidth = 3;
  
        ctx.strokeText(bottomText, canvas.width / 2, bottomTextY);
        ctx.fillText(bottomText, canvas.width / 2, bottomTextY);
  
        // Preview mise Ã  jour ici
        const dataURL = canvas.toDataURL("image/png");
        setPreview(dataURL); // pour affichage live
        console.log("kd", dataURL);
        
  
        canvas.toBlob((blob) => {
          if (blob) resolve(blob);
          else reject(new Error("Ã‰chec gÃ©nÃ©ration meme"));
        }, "image/png");
      };
  
      img.onerror = reject;
      img.src = URL.createObjectURL(imageFile);
      console.log(img.src);
    //   setPreview(img.src);
      setSelectedImage(imageFile)
      
    });
  };
  
  useEffect(() => {
    if (selectedImage && topText && bottomText) {
      generateMemeImage(selectedImage, topText, bottomText)
        .then(blob => {
          const url = URL.createObjectURL(blob);
          setPreview(url);
        });
    }
  }, [selectedImage, topText, bottomText]); 

const generateFinalMemeAndUpload = async () => {
    if (!memeRef.current) return;
  
    setIsUploading(true);
    try {
    const blob = await generateMemeImage(selectedImage, topText, bottomText);
  
      const formData = new FormData();
      formData.append('image', blob, 'meme.png');
  
      const response = await axios.post("http://localhost:5000/api/upload", formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
  
      setImageUrl(response.data.imageUrl);
      setCloudinaryId(response.data.cloudinaryId);
      toast.success('meme gÃ©nÃ©rÃ© et uploadÃ©!');
      return true;
    } catch (error) {
      console.error('Erreur lors de la gÃ©nÃ©ration:', error);
      toast.error('Erreur pendant le rendu du meme');
      return false;
    } finally {
      setIsUploading(false);
    }
  };
  
const handleSaveMeme = async () => {
    if (!title.trim()) {
      toast.error('Veuillez ajouter un titre');
      return;
    }
    const success = await generateFinalMemeAndUpload();
    if (!success || !imageUrl) {
      toast.error("L'image avec le texte n'a pas pu Ãªtre gÃ©nÃ©rÃ©e.");
      return;
    }
    setIsSaving(true);
    try {
      const memeData = {
        title: title.trim(),
        imageUrl,
        cloudinaryId,
      };
      await axios.post('http://localhost:5000/api/memes', memeData);
      toast.success('meme sauvegardÃ© avec succÃ¨s!');
      resetForm();
    } catch (error) {
      console.error('Erreur sauvegarde:', error);
      toast.error('Erreur lors de la sauvegarde');
    } finally {
      setIsSaving(false);
    }
  };
  
const handleDownload = async () => {
    if (!memeRef.current) return;

    try {
      const canvas = await html2canvas(memeRef.current, {
        backgroundColor: null,
        scale: 2,
      });

      const link = document.createElement('a');
      link.download = `${title || 'meme'}.png`;
      link.href = canvas.toDataURL();
      link.click();
      
      toast.success('meme tÃ©lÃ©chargÃ©!');
    } catch (error) {
      console.error('Erreur tÃ©lÃ©chargement:', error);
      toast.error('Erreur lors du tÃ©lÃ©chargement');
    }
  };

  const resetForm = () => {
    setSelectedImage(null);
    setImageUrl('');
    setCloudinaryId('');
    setTopText('');
    setBottomText('');
    setTitle('');
  };

  const handleShare = async () => {
    if (!preview) return;
  
    try {
      const response = await fetch(preview);
      const blob = await response.blob();
      const file = new File([blob], 'meme.png', { type: blob.type });
  
      if (navigator.canShare && navigator.canShare({ files: [file] })) {
        await navigator.share({
          title: `meme: ${title}`,
          text: `Regardez ce meme que j'ai crÃ©Ã© : ${title}`,
          files: [file],
        });
      } else {
        navigator.clipboard.writeText(preview);
        toast.success('URL copiÃ©e dans le presse-papier (partage direct non supportÃ©)');
      }
    } catch (error) {
      console.error("Erreur lors du partage:", error);
      toast.error("Partage impossible.");
    }
  };
  

    return (
        <div className="meme-generator px-4 py-8 max-w-4xl mx-auto text-white">
          <h1 className="text-4xl font-bold mb-8 text-center">ðŸŽ¨ CrÃ©ateur de Memes</h1>
      
          <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 shadow-2xl">
            {/* Section Upload */}
            <h2 className="text-2xl font-semibold mb-4">1. Choisir une image</h2>
            <div className="mb-6">
              <label htmlFor="image-upload" className="cursor-pointer block w-full text-center py-4 border-2 border-dashed border-white/50 rounded-xl hover:bg-white/10 transition">
                {isUploading ? "ðŸ“¤ Upload en cours..." : "ðŸ“¸ Cliquez pour sÃ©lectionner une image"}
              </label>
              <input
                id="image-upload"
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                className="hidden"
              />
            </div>
      
            {/* Section Texte */}
            {selectedImage && (
              <>
                <h2 className="text-2xl font-semibold mb-4">2. Ajouter du texte</h2>
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-6">
                  <input
                    type="text"
                    placeholder="Titre du meme"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    className="bg-white/20 border border-white/30 text-white px-4 py-2 rounded-lg placeholder-white sm:col-span-2"
                  />
                  <input
                    type="text"
                    placeholder="Texte du haut"
                    value={topText}
                    onChange={(e) => setTopText(e.target.value)}
                    className="bg-white/20 border border-white/30 text-white px-4 py-2 rounded-lg placeholder-white "
                  />
                  <input
                    type="text"
                    placeholder="Texte du bas"
                    value={bottomText}
                    onChange={(e) => setBottomText(e.target.value)}
                    className="bg-white/20 border border-white/30 text-white px-4 py-2 rounded-lg placeholder-white "
                  />
                </div>
              </>
            )}
      
            {/* AperÃ§u */}
            {selectedImage && (
              <>
                <h2 className="text-2xl font-semibold mb-4">3. AperÃ§u</h2>
                <div className="relative bg-white rounded-xl overflow-hidden shadow-xl p-4 max-w-md mx-auto mb-6">
                  <div className="relative">
                    <img src={preview} alt="preview" className="w-full rounded-md" />
                  </div>
                </div>
      
                {/* Actions */}
                <div className="flex flex-wrap gap-4 justify-center">
                  <button onClick={handleSaveMeme} disabled={isSaving} className="action-btn save-btn">
                    {isSaving ? 'ðŸ’¾ Sauvegarde...' : 'ðŸ’¾ Sauvegarder'}
                  </button>
                  <button onClick={handleDownload} className="action-btn download-btn">
                    ðŸ“¥ TÃ©lÃ©charger
                  </button>
                  <button onClick={handleShare} className="action-btn share-btn">
                    ðŸ“¤ Partager
                  </button>
                  <button onClick={resetForm} className="action-btn reset-btn">
                    ðŸ”„ Nouveau
                  </button>
                </div>
              </>
            )}
          </div>
        </div>
      );

    }
export default MemeGenerator;
